mapper.select:
SELECT @ROWNUM := @ROWNUM +1 as ROWNUM, l.IDX, l.USER_ORIGIN, l.STORE_ORIGIN, l.LINEUP_COUNT, l.LINEUP_YN, l.LINEUP_DATE, l.REGISTER_DATE, l.REGISTER_ID, l.UPDATE_DATE, l.UPDATE_ID, l.LINEUP_VISIT, (select NAME from hh_user where ORIGIN_NUM = l.USER_ORIGIN) AS LINEUP_NM, (select TEL from hh_user where ORIGIN_NUM = l.USER_ORIGIN) AS LINEUP_TEL, (select STORE_NM from hh_store where idx = l.STORE_ORIGIN) AS STORE_NM FROM HH_LINEUP l, (select @rownum := 0) tmp WHERE 1=1
AND l.STORE_ORIGIN = #{store}
AND l.USER_ORIGIN = #{idx}
AND l.LINEUP_VISIT <> 0 AND l.LINEUP_YN = #{path} ORDER BY l.LINEUP_DATE DESC
AND l.LINEUP_DATE > #{today}
AND (l.LINEUP_DATE < #{todayEnd} OR l.LINEUP_DATE = #{todayEnd}) AND l.LINEUP_VISIT = 0
AND l.LINEUP_YN = #{path} ORDER BY l.LINEUP_DATE ASC
LIMIT #{startCount}, #{endCount}

SELECT COUNT(*) FROM HH_LINEUP
WHERE 1=1

	AND STORE_ORIGIN = #{store}


	AND USER_ORIGIN = #{idx}


	
		AND LINEUP_VISIT <> 0
	
	
