			AND (LINEUP_DATE < #{todayEnd} OR LINEUP_DATE = #{todayEnd})
		
		AND LINEUP_VISIT = 0
	

AND LINEUP_YN =  #{path}

SELECT COUNT(*) FROM HH_LINEUP
WHERE STORE_ORIGIN = #{store}
AND LINEUP_DATE > #{today}
AND (LINEUP_DATE < #{todayEnd} OR LINEUP_DATE = #{todayEnd})
AND LINEUP_VISIT = 0

