mapper.select:
SELECT l.IDX, l.USER_ORIGIN, l.STORE_ORIGIN, l.LINEUP_COUNT, l.LINEUP_YN, l.LINEUP_DATE, l.REGISTER_DATE, l.REGISTER_ID, l.UPDATE_DATE, l.UPDATE_ID, l.LINEUP_VISIT, l.SMS_ID, (select NAME from hh_user where ORIGIN_NUM = l.USER_ORIGIN) AS LINEUP_NM, (select TEL from hh_user where ORIGIN_NUM = l.USER_ORIGIN) AS LINEUP_TEL FROM HH_LINEUP l
WHERE l.STORE_ORIGIN = #{store} AND LINEUP_DATE > #{today} AND (LINEUP_DATE < #{todayEnd} OR LINEUP_DATE = #{todayEnd}) AND LINEUP_VISIT = 0 ORDER BY LINEUP_DATE ASC LIMIT 1
WHERE IDX = #{idx}

mapper.update:
UPDATE HH_LINEUP SET
	SMS_ID = #{sms_id}
WHERE IDX = #{idx}
